mapper.insert:
INSERT INTO DC_MEMBER_TB VALUES(
	SEQ_MEMBER.NEXTVAL, #{email}, #{nickName}, 
	#{password}, SYSDATE, 0, #{locationNo}, 1, 'STATUS001', null, #{name}
)

mapper.select:
		SELECT COUNT(*)
     	FROM DC_MEMBER_TB
    	WHERE NICKNAME = #{nickName}

		SELECT COUNT(*)
     	FROM DC_MEMBER_TB
    	WHERE EMAIL = #{email}
