mapper.select:
		SELECT COUNT(*)
     	FROM DC_MEMBER_TB
    	WHERE NICKNAME = #{nickName}

		SELECT COUNT(*)
     	FROM DC_MEMBER_TB
    	WHERE EMAIL = #{email}

mapper.update:
UPDATE DC_MEMBER_TB SET
NICKNAME = #{nickName}, PASSWORD = #{password}, 
LOCATIONNO = #{locationNo}, ADDRESS=#{address}, DEPOSIT=#{deposit}, BANK=#{bank}
WHERE NO = #{no}
